import "./App.css";
import { Component } from "react";
class App extends Component{
  constructor() {
   super();
    this.state={
      age: 3,
      name: [ {text : 'andreea'},  {text : 'andrei'}],
      students: [{name: 'Andreea ', age: 22 }, {name:'Tudor' , age: 24}]
    };
  }
  plusName(event){

    console.log(event.target)
  }
  render(){
    return( <div>
      <h1>Let`s gooo</h1>
      <p>
        {this.state.age}
      </p>
      <p>
        {this.state.name.map((x)=>{
          return (<li>{x.text}</li>)
        })}
      </p>
      <p>
        {this.state.students.map((x)=>{
          return(<li>{x.name} is {x.age} years old </li>)
        })}
      </p>

      <input placeholder="Enter your name" onChange={this.plusName}></input>

    </div>)
  }
}
export default App;
