import React, { Component } from 'react'

class FormComponent extends Component {
    constructor(props) {
        super(props);
        console.log("SADd", props)
        this.state = { todoWish1: '', todoWishRon: '', errorInputRon: false }  ///like useState(0), astea sunt locale si le voi transmite la Parinte cu functia  addWishSubmit
    }
    onWishTextChange = (e) => {   /* retin dorinta pentru a o putea pune in usestate */
        this.setState({ todoWish1: e.target.value }) ///like setTodoWish(todowish: 4)
    }
    onMoneyTextChange = (e) => {      /* retin dorinta Ron pentru a o putea pune in usestate */
        if (!/^[0-9]+$/.test(e.target.value))
            this.setState({ errorInputRon: true })
        else {
            this.setState({ errorInputRon: false, todoWishRon: e.target.value })
        }
    }

    render() {
        const { todoWish1, todoWishRon, errorInputRon } = this.state;
        return (
            <form id='formId' onSubmit={(e) => {
                e.preventDefault();
                this.props.addWishSubmit({ todoWish1, todoWishRon, errorInputRon })
                this.setState({ todoWish1: '', todoWishRon: '' })
            }
            } >
                
                <input id="inputWish" placeholder='Add your wish' onChange={this.onWishTextChange} value={todoWish1} />
                <input id="inputCash" placeholder='Add $' onChange={this.onMoneyTextChange} value={todoWishRon} />
                {
                    errorInputRon ? <p> Please complete corectly all fields!</p> : ""
                }
                <button id='buttonAdd' type='submit'>Add</button>
            </form>
        )
    }
}
export default FormComponent
