import React from 'react'

function WishComponent({ id, wishText , edit ,editInputWish,setUpdateWish}) {

    return (
        <div className='addWish' key={id}>
            {
                edit ?
                    <>
                        <input value={wishText} onChange={(e) => editInputWish(e,id)} />
                        <button onClick={() => setUpdateWish(wish.id)}>Update Wish</button>
                    </> : <p className={wish.finishWish ? 'finishText' : ''}>My wish: {wishText} </p>
            }





        </div>

    )
}

export default WishComponent


