import React, { useState } from 'react'
import "./projectForm.css"
function ProjectForm() {

    const [wishes, setWishes] = useState([]);
    const [todoWish, setTodoWish] = useState('')  /* ce wish introduc  in input*/
    const [todoWishRon, setTodoWishRon] = useState('')  /* ce wish RON introduc  in input*/
    const [totalAmount,setTotalAmount]=useState(0);
    const [startAmount,setStartAmount]=useState(0)

    function random(){
        return Math.floor(Math.random() * 50444);
    }

    function addWishSubmit(e) {   /* adaug in vector dorinta si banii */
        console.log(e)
        e.preventDefault()
        setWishes([
            ...wishes, {
                wishText: todoWish, money: todoWishRon ,id : random() , startWish : false 
            }
        ])
        setTodoWish('')
        setTodoWishRon('')

    }
    function addWish(w) {   /* retin dorinta pentru a o putea pune in usestate */
        console.log(w)
        setTodoWish(w.target.value)
    }
    function addMoney(w) {    /* retin dorinta Ron pentru a o putea pune in usestate */
        console.log(w)
        setTodoWishRon(w.target.value)
    }

    function startwish(id){

      const startWish = wishes.map((x)=>{
           if(x.id==id)
               return {...x, startWish: !startAmount }    /*daca startWish e true, atunci schimbam valoarea */
               else return x;        /* daca nu, returnam si se va forma tot un array de obiecte */

       })
       setWishes(startWish)
    console.log ( "DASD ",{wishes})
    }   


    console.log({wishes})
    return (
        <>
            <div id="amount">
                Started amount:{startAmount}
                <br>
                </br>
                ...........................<br>
                </br>
                Total amount: {totalAmount}
            </div>

            <div id="nameAndData">@AndreiAndreea- 11/03/2022</div>

            <div>
                <h1>
                    Wish List
                </h1>
                <div>
                    <form id='formId' onSubmit={addWishSubmit}>

                        <input id="inputWish" placeholder='Add your wish' onChange={addWish} value={todoWish} />
                        <input id="inputCash" placeholder='Add $' onChange={addMoney} value={todoWishRon} />
                        {/* Cand aplic onchange, automat voi transmite ce am in value si asa voi seta */}
                        <button id='buttonAdd' type='submit'>Add</button>

                    </form>
                    <div >
                        {
                            wishes.map((i) => {
                                return <div className='addWish'> My wish: {i.wishText}  = {i.money} ron
                                    <br>
                                    </br>
                                    <button onClick={()=> startwish(i.id)}>Start Wish</button>
                                    <button>Edit Wish</button>
                                    <button>Delete Wish</button>                              
                                    <button>Finish Wish</button>
                                </div>
                            })
                        }
                    </div>
                </div>

            </div>




        </>

    )
}

export default ProjectForm
