import React, { useState } from 'react'
import "./VarutzuProject.css"

function VarutzuProject() {
    const [wishes, setwishes] = useState([
        { wish: 'Money', money: 10 },
        { wish: 'Money1', money: 101 }])

    const [todoWish, setTodoWish] = useState('')



    function addWish(e) {
        console.log(e.target)
        setTodoWish(e.target.value)

    }
    function addWishSubmit(e) {
        e.preventDefault();
        setwishes([
            ...wishes,
            {
                wish: todoWish, money: 1
            }
        ])
console.log(wishes)
    }

    return (

        <>
            <div id="startedAmount">
                Started amount:
                <br>
                </br>
                ...........................<br>
                </br>
                Total amount:
            </div>
            <div id="name">  @AndreiAndreea- 11/03/2022</div>
            <header>
                <h1>Wish List</h1>
                <div id='form'>
                    <form onSubmit={addWishSubmit}>
                        <input id='input1' placeholder='  Add Wish' onChange={addWish} value={todoWish}></input>
                        <input id='input2' placeholder='  Add ron'></input>
                        <button>
                            Add Wish!
                        </button>
                    </form>
                    <div>
                        {wishes.map((x) => {
                            return <div>{x.wish} : {x.money}
                            </div>
                        })}
                    </div>

                </div>


            </header>


        </>
    )
}

export default VarutzuProject
