import React, { useState } from 'react'
import "./projectForm.css"
function ProjectForm() {

    const [wishes, setWishes] = useState([]);
    const [todoWish, setTodoWish] = useState('')  /* ce wish introduc  in input*/
    const [todoWishRon, setTodoWishRon] = useState('')  /* ce wish RON introduc  in input*/
    const [totalAmount, setTotalAmount] = useState(0);
    const [startAmount, setStartAmount] = useState(0)
    const [zeroWishes,setZeroWishes]=useState(true)
    function random() {
        return Math.floor(Math.random() * 50444);
    }

    function addWishSubmit(e) {   /* adaug in vector dorinta si banii */
        console.log(e)
        e.preventDefault()
        setWishes([
            ...wishes, {
                wishText: todoWish, money: todoWishRon, id: random(), startWish: false, finishWish: false , delete : false
            }
        ])
        setTodoWish('')
        setTodoWishRon('')

    }
    function addWish(w) {   /* retin dorinta pentru a o putea pune in usestate */
        console.log(w)
        setTodoWish(w.target.value)
    }
    function addMoney(w) {    /* retin dorinta Ron pentru a o putea pune in usestate */
        console.log(w)
        setTodoWishRon(w.target.value)
    }

    function startwish(id) {

        const start = wishes.map((x) => {
            if (x.id === id) {
                if (!x.startWish) { /* daca e false , adaugam la startAmount */
                    setStartAmount(parseInt(x.money) + startAmount)
                    setTotalAmount(parseInt(x.money) + totalAmount)
                    return { ...x, startWish: !x.startWish }
                }
                else {   /* daca nu , doar mergem mai departe cu return ul de obiecte */
                    return x;
                }

            }
            /*daca startWish e true, atunci schimbam valoarea */
            else return x;        /* daca nu, returnam si se va forma tot un array de obiecte */

        })
        setWishes(start)   /* vom updata array ul de obiecte cu noul array de obiecte */
        console.log("DASD ", { wishes })
    }
    function finishWish(id) {
        const finish = wishes.map((x) => {
            if (id === x.id) {
                if (!x.finishWish)  /* daca finishWish e false ,verificam daca startwish e True */
                    if (x.startWish)  /* daca e true, inseamna ca scadem din StartAmount */ {
                        setStartAmount(startAmount - x.money)
                        return { ...x, startwish: !x.startWish }
                    }
                    else
                return x;
            }
            else
            return x;
        })
        setWishes(finish)


    }
  function deleteWish(id){

    const newArray=wishes.map((x)=>{
        if(id !== x.id)
            return x;
            

            
    })
    if(wishes.length ===0 )
      {    setZeroWishes(false) 
           setWishes([]) }
    else{
        setWishes(newArray)
        setZeroWishes(true) 
    }


  }
    console.log({ wishes })
    return (
        <>
            <div id="amount">
                Started amount:{startAmount}
                <br>
                </br>
                ...........................<br>
                </br>
                Total amount: {totalAmount}
            </div>

            <div id="nameAndData">@AndreiAndreea- 11/03/2022</div>

            <div>
                <h1>
                    Wish List
                </h1>
                <div>
                    <form id='formId' onSubmit={addWishSubmit}>

                        <input id="inputWish" placeholder='Add your wish' onChange={addWish} value={todoWish} />
                        <input id="inputCash" placeholder='Add $' onChange={addMoney} value={todoWishRon} />
                        {/* Cand aplic onchange, automat voi transmite ce am in value si asa voi seta */}
                        <button id='buttonAdd' type='submit'>Add</button>

                    </form>
                    <div >
                        zeroWishes
                        {
                            wishes.map((i) => {
                                return <div className='addWish'> My wish: {i.wishText}  = {i.money} ron
                                    <br>
                                    </br>
                                    <button onClick={() => startwish(i.id)}>Start Wish</button>
                                    <button>Edit Wish</button>
                                    <button onClick={()=>deleteWish(i.id)}>Delete Wish</button>
                                    <button onClick={() => finishWish(i.id)}>Finish Wish</button>
                                </div>
                            })
                        }
                    </div>
                </div>

            </div>




        </>

    )
}

export default ProjectForm
