import React, { Component } from 'react'

class FormComponent extends Component {
    constructor() {
        super();
        this.state = { todoWish: '', todoWishRon: '' }
    }
    onWishTextChange(e) {   /* retin dorinta pentru a o putea pune in usestate */

        setTodoWish(e.target.value)
    }
    onMoneyTextChange(e) {      /* retin dorinta Ron pentru a o putea pune in usestate */
        if (!/^[0-9]+$/.test(e.target.value))
            setErrorInputRon(true)
        else {
            setErrorInputRon(false)
            setTodoWishRon(e.target.value)
        }
    }

    render() {

        const { todoWish, todoWishRon } = this.state;
        return (
            <form id='formId' onSubmit={this.props.addWishSubmit} >

                <input id="inputWish" placeholder='Add your wish' onChange={this.props.onWishTextChange} value={todoWish} />
                <input id="inputCash" placeholder='Add $' onChange={this.props.onMoneyTextChange} value={todoWishRon} />
                {
                    this.props.errorInputRon ? <p> Please complete corectly all fields!</p> : ""
                }
                <button id='buttonAdd' type='submit'>Add</button>

            </form>
        )

    }

}
export default FormComponent
