import React, { useState } from 'react'
import "./VarutzuProject.css"

function VarutzuProject() {
    const [wishes, setwishes] = useState([])
    const [todoWish1, setTodoWish1] = useState('')
    const [todoWish2, setTodoWish2] = useState('')
    const [totalSuma, setTotalSuma] = useState(0)
    const [startedSuma, setStartedSuma] = useState(0)


    function addWish1(e) {
        console.log(e.target)
        setTodoWish1(e.target.value)

    }
    function addWish2(e) {
        console.log(e.target)
        setTodoWish2(e.target.value)

    }
    function addWishSubmit(e) {
        e.preventDefault();
        if (todoWish2 != parseInt(todoWish2) || todoWish2 == '' || todoWish1 == '') {
            var x = document.getElementById('alerta')
            x.style.display = 'block'


        }
        else {
            var today = new Date();


            let date = new Date()
            let day = date.getDate();
            let month = date.getMonth() + 1;
            let year = date.getFullYear();
            var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds()
            let fullDate = `${day}/${month}/${year}         ${time}`;

            var x = document.getElementById('alerta')
            x.style.display = 'none'
            function randomNr() {
                return Math.floor(Math.random() * 50444);
            }
            setwishes([
                ...wishes,
                {
                    wish: todoWish1, money: todoWish2, idStart: randomNr(), doneStart: false, doneFinish: false, data: fullDate ,doneDelete :false
                }
            ])

            var s = parseInt(todoWish2) + totalSuma;
            setTotalSuma(s)

        }

        setTodoWish1('')
        setTodoWish2('')


    }
    function butonStart(e) {
        console.log("id: ", e)


        const startW = wishes.map((i) => {
            if (i.idStart === e) {

                if (!i.doneStart) {
                    console.log(i.doneStart)
                    setStartedSuma(parseInt(i.money) + startedSuma)
                    return { ...i, doneStart: !i.doneStart }
                }
                else return i

            }

            else
                return i
        })

        setwishes(startW)

    }

    function butonFinish(e) {
        console.log("id: ", e)


        const doneW = wishes.map((i) => {
            if (i.idStart === e) {

                if (!i.doneFinish & i.doneStart) {
                    console.log(i.doneFinish)
                    setStartedSuma(startedSuma - parseInt(i.money))
                    return { ...i, doneFinish: !i.doneFinish }
                }
                else return i

            }

            else
                return i
        })

        setwishes(doneW)



    }

    function buttonDelete(e) {
        const doneD = wishes.map((i) => {
            if (i.idStart === e) {

                if (!i.doneDelete) 
                    return { ...i, doneFinish: !i.doneFinish }
                
                else return i

            }

            else
                return i
        })

        setwishes(doneD)
        
    }


    return (

        <>
            <div id="startedAmount">
                Started amount: {startedSuma}
                <br>
                </br>
                ...........................<br>
                </br>
                Total amount: {totalSuma}
            </div>
            <div id="name">  @AndreiAndreea- 11/03/2022</div>
            <header>
                <h1>Wish List</h1>
                <div id='form'>
                    <div id="formdiv">
                        <form onSubmit={addWishSubmit}>
                            <input id='input1' placeholder='  Add Wish' onChange={addWish1} value={todoWish1}></input>
                            <input id='input2' placeholder='  Add ron' onChange={addWish2} value={todoWish2}></input>
                            <button>
                                Add Wish!
                            </button>
                            <p id='alerta' style={{ display: 'none' }}>Enter a number, not a string !!!</p>
                        </form>
                    </div>
                    <div id="divWishes"   >
                        {wishes.map((x) => {
                            return <div id="divWishes1" key={x.idStart}  style={{display: x.doneDelete ? 'none' : 'block' }}>
                                <p className={x.doneFinish ? 'done' : null} >
                                    {x.wish} : {x.money} ron
                                    <br>
                                    </br>
                                </p>
                                Wish added on:  {x.data}
                                <br>
                                </br>
                                <div id='btn'>
                                    <button >Edit Wish</button>
                                    <button onClick={() => buttonDelete(x.idStart)}>Delete Wish</button>
                                    <button onClick={() => butonStart(x.idStart)}>Start wish</button>
                                    <button onClick={() => butonFinish(x.idStart)} >Finish Wish</button>
                                </div>
                            </div>
                        })}

                    </div>

                </div>


            </header>


        </>
    )
}

export default VarutzuProject
