import "./App.css";
import { Component } from "react";
class App extends Component {
  constructor() {
    super();
    this.state = {
      age : 3,
      todo: "",
      name: [{ text: "andreea" }, { text: "andrei" }],
      students: [
        { name: "Andreea ", age: 22 },
        { name: "Tudor", age: 24 },
      ],
    };
  }

  plusName = () => {
    this.setState({age: "blue"});
  }


  render() {
    return (
      <div>
        <h1>Let`s gooo</h1>
        <p>{this.state.age}</p>
        <p>
          {this.state.name.map((x) => {
            return <li>{x.text}</li>;
          })}
        </p>

        <p id="demo"></p>
        <p>
          {this.state.students.map((x) => {
            return (
              <li>
                {x.name} is {x.age} years old{" "}
              </li>
            );
          })}
        </p>

        <form>
          <input type="text" name="fname" onChange={this.plusName}></input>{" "}
          <br></br>
          <label>Add</label>
        </form>
      </div>
    );
  }
}
export default App;
